function PostShift() {

  // Make a POST request with a JSON payload.
 var data = {
   'donarName': 'Bob Smith',
   'recipientName': 'Bobs Burgers'

 };
 var options = {
   'method' : 'post',
   'contentType': 'application/json',
   // Convert the JavaScript object to a JSON string.
   'payload' : JSON.stringify(data)
 };
 var response = UrlFetchApp.fetch('http://shiftservice-development.qjasxcqfqt.us-west-2.elasticbeanstalk.com/tcfj/v1/shifts', options);

  Logger.log(response.getResponseCode());

}
